                        category: "Sports"
                    },
                    {
                        user:"vishthefish",
                        post_content: "Coding group for 293?",
                        category: "Misc"
                    }
                ]
       };
   }

  render() {

    const tabData = this.state.data;
    return (
        <ListGroup className="All">

            {
              this.state.data.map(function(data) {
                return <ListGroupItem key={data.user}>{data.user}: {data.post_content} </ListGroupItem>
              })
            }
          </ListGroup>

    );
  }
}

export default All;
